const ShowTask = () => {
    //accessing trans (array of objects)
    const{ trans }= useContext(GlobalContext);
    return (
        <div className='task-layout'>
           <h1>Show Tasks</h1> 
           <div className='task-input'>
                <TaskInput/>
                {console.log('trans is',trans) }
           </div>
           <div>
               <ListTask/>
            </div>

        </div>
    )
}

export default ShowTask
